"use strict";const e=require("../common/vendor.js");exports.formatTimeDifference=function(e){const o=Date.now()-e,n=6e4,t=36e5,r=24*t,i=30*r;if(o<n)return"1分钟";if(o<t){return`${Math.floor(o/n)}分钟`}if(o<r){return`${Math.floor(o/t)}小时`}if(o<i){return`${Math.floor(o/r)}天`}if(o<3*i){return`${Math.floor(o/i)}月`}return null},exports.gotoHome=function(){e.index.showModal({title:"提示",content:"页面有误将返回首页",showCancel:!1,success:function(o){o.confirm&&e.index.switchTab({url:"/pages/index/index"})}})};
